import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Asegúrate de tener los estilos adecuados

const Navbar = () => {
  const { cart, removeFromCart } = useCart();

  // Función para manejar la eliminación de una cantidad específica
  const handleRemove = (productId, quantity) => {
    removeFromCart(productId, quantity);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">Your Shop</Link>
        <div className="navbar-cart-dropdown">
          <button className="navbar-cart-button">Cart</button>
          <div className="cart-dropdown">
            <h3>Your Cart</h3>
            {cart.length === 0 ? (
              <p>Your cart is empty.</p>
            ) : (
              <ul className="cart-dropdown-list">
                {cart.map(product => (
                  <li key={product._id} className="cart-dropdown-item">
                    <img src={`${process.env.PUBLIC_URL}/assets/${product.image}`} alt={product.name} className="cart-dropdown-image" />
                    <div className="cart-dropdown-info">
                      <h4 className="cart-dropdown-name">{product.name}</h4>
                      <p className="cart-dropdown-price">${product.price.toFixed(2)}</p>
                      <div className="cart-dropdown-quantity-controls">
                        <span className="cart-dropdown-quantity">{product.quantity}</span>
                        <button
                          className="cart-dropdown-remove-button"
                          onClick={() => handleRemove(product._id, 1)}
                        >
                          Remove 1
                        </button>
                        <button
                          className="cart-dropdown-remove-button"
                          onClick={() => handleRemove(product._id, product.quantity)}
                        >
                          Remove All
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
            <Link to="/cart" className="cart-dropdown-view-cart">View Cart</Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
