import React, { useEffect, useState } from 'react';
import axios from '../axios'; // Asegúrate de que la configuración de axios esté correcta
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true); // Agregar estado de carga
  const [error, setError] = useState(null); // Agregar estado de error
  const { id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/products/${id}`);
        setProduct(response.data);
        setLoading(false); // Datos cargados
      } catch (error) {
        console.error('Error fetching product:', error);
        setError('Error fetching product'); // Manejo de error
        setLoading(false); // Datos cargados con error
      }
    };

    fetchProduct();
  }, [id]);

  if (loading) {
    return <p className="loading-message">Loading...</p>;
  }

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  return (
    <div className="product-detail-container">
      {product ? (
        <div className="product-detail-content">
          <div className="product-image">
            <img src={`${process.env.PUBLIC_URL}/assets/${product.image}`} alt={product.name} />
          </div>
          <div className="product-info">
            <h1 className="product-name">{product.name}</h1>
            <p className="product-description">{product.description}</p>
            <p className="product-price">${product.price.toFixed(2)}</p>
            <button className="add-to-cart-button">Add to Cart</button>
          </div>
        </div>
      ) : (
        <p className="no-product-message">Product not found</p>
      )}
    </div>
  );
};

export default ProductDetail;
