import React from 'react';
import { useCart } from '../context/CartContext'; 
import './Cart.css';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity } = useCart(); 

  const handleRemove = (productId) => {
    if (window.confirm("Are you sure you want to remove this item from your cart?")) {
      removeFromCart(productId);
    }
  };

  const handleQuantityChange = (productId, e) => {
    const newQuantity = parseInt(e.target.value, 10);
    if (newQuantity > 0) {
      updateQuantity(productId, newQuantity);
    } else {
      handleRemove(productId); // Remove item if quantity is zero
    }
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul className="cart-list">
          {cart.map(product => (
            <li key={product._id} className="cart-item">
              <img src={product.imageUrl} alt={product.name} className="cart-image" />
              <div className="cart-info">
                <h3 className="cart-name">{product.name}</h3>
                <p className="cart-price">${product.price.toFixed(2)}</p>
                <p className="cart-quantity">
                  Quantity:
                  <input
                    type="number"
                    min="1"
                    value={product.quantity}
                    onChange={(e) => handleQuantityChange(product._id, e)}
                    className="quantity-input"
                  />
                </p>
                <button 
                  className="remove-button" 
                  onClick={() => handleRemove(product._id)}
                >
                  Remove
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
